category.item:
LevelFuncs.OnLoop = function() Add{$functionName}() end
function Add{$functionName}()
local staticStartSlots = {literal}{{/literal}{$staticSlot}{literal}}{/literal} local moveableStartSlots = {literal}{{/literal}{$moveableSlot}{literal}}{/literal} local xPosTolerance = {$xPosTolerance}
local yPosTolerance = {$yPosTolerance}
local zPosTolerance = {$zPosTolerance}
local xSpeed = {$xSpeed}
local ySpeed = {$ySpeed}
local zSpeed = {$zSpeed}
local gravityTolerance = {$gravityTolerance}
local rotationTolerance = {$rotationTolerance}
-- ########### FROM HERE ON PLEASE DO NOT CHANGE ANY VALUES IN THE CODE ########################## local objectsForParticles = {}
for index, staticStartSlot in pairs(staticStartSlots) do local statics = GetStaticsBySlot(staticStartSlot) for index, static in pairs(statics) do table.insert(objectsForParticles, static) end end
for index, moveableStartSlot in pairs(moveableStartSlots) do local moveables = GetMoveablesBySlot(moveableStartSlot) for index, moveable in pairs(moveables) do table.insert(objectsForParticles, moveable) end end
for index, objectForParticle in pairs(objectsForParticles) do
-- Calculate start positions local vec3Pos = objectForParticle:GetPosition()
local xPos = vec3Pos.x local yPos = vec3Pos.y local zPos = vec3Pos.z
if xPosTolerance > 0 then xPos = vec3Pos.x+math.random(xPosTolerance)-math.random(xPosTolerance) end
if yPosTolerance > 0 then yPos = vec3Pos.y+math.random(yPosTolerance)-math.random(yPosTolerance) end
if zPosTolerance > 0 then zPos = vec3Pos.z+math.random(zPosTolerance)-math.random(zPosTolerance) end
-- Calculate Velocity local xVelo = 0 + math.random(xSpeed) - math.random(xSpeed) local yVelo = 0 + math.random(ySpeed) - math.random(ySpeed) local zVelo = 0 + math.random(zSpeed) - math.random(zSpeed)
for i=1,math.random(1,{$density}) do EmitParticle( Vec3(xPos, yPos, zPos), --start position Vec3(xVelo, yVelo, zVelo), -- velocity {$spriteIndex}, -- spriteIndex math.random(0-gravityTolerance, 0+gravityTolerance), -- gravity math.random(0-rotationTolerance, 0+rotationTolerance), -- rotation Color({$colorFromRed},{$colorFromGreen},{$colorFromBlue}), -- startColor Color({$colorToRed},{$colorToGreen},{$colorToBlue}), -- endColor TEN.Effects.BlendID.{foreach from=$blendModes item=blendMode key=key}{if $selectedBlendMode == $key}{$blendMode}{/if}{/foreach}, -- blendMode math.random({$startSizeTolerance}), -- startSize math.random({$endSizeTolerance}), -- endSize math.random({$lifeTime}), -- lifetime {foreach from=$damageModes item=damageMode key=key}{if $selectedDamageMode == $key}{$damageMode}{/if}{/foreach}, -- damage {foreach from=$poisionModes item=poisionMode key=key}{if $selectedPoisionMode == $key}{$poisionMode}{/if}{/foreach} -- poison ) end end end
